workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;
import etracs2.groovy.CommonUtil

public class RPTDelinquencyNopas
{

    @Service('RPTDelinquencyService')
    def svc;

    @Service( 'ReportParameterService')
    def paramSvc

    @Service( 'DateService')
    def dtSvc

    def entity; 
    def mode
    def objid;
    def document

    public void init() {
        mode = 'init';
        entity = svc.open(objid)
        entity.doctype = document.code
        entity.doctypedesc = document.description
        entity.parentid = entity.objid
    }

    public def open() {
        entity = svc.open(entity.objid)
        mode = 'saved'
        return "default";
    }

    public void save() {
        entity = svc.supersedeDoc( entity )
        mode = 'saved';
        MsgBox.alert("Successfully saved.    ")
    }

    def preview() {
        report.viewReport()
        return "preview";
    }

    def back() {
        return "default";
    }

    def sdf = new java.text.SimpleDateFormat( "MMMMM dd, yyyy")
    def sdf1 = new java.text.SimpleDateFormat( "yyyy-MM-dd")

    def report = [
        getReportName : { return 'demandletters/noticeofpublicationandauctionsale' }, 
        getReportData : { 
            entity.dt1stpublication = sdf.format( sdf1.parse( entity.dt1stpublication ) )
            entity.dt2ndpublication = sdf.format( sdf1.parse( entity.dt2ndpublication ) )
            entity.expirydate = sdf.format( sdf1.parse( entity.expirydate ) )
            entity.auctiondate= sdf.format( sdf1.parse( entity.auctiondate ) )
            entity.parenttxndate = sdf.format( sdf1.parse( entity.parenttxndate ) )
            return entity 
        },
        getParameters : {
            return paramSvc.getStandardParameter( OsirisContext.env.USERNAME )
        }
    ]  as ServerReportModel

}
