workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;
import etracs2.groovy.CommonUtil

public class RPTDelinquencyNOR
{

    @Service('RPTDelinquencyService')
    def svc;

    @Service( 'ReportParameterService')
    def paramSvc

    @Service( 'DateService')
    def dtSvc


    def entity; 
    def mode
    def objid;
    def document

    public void init() {
        mode = 'init';
        entity = svc.open(objid)
        entity.doctype = document.code
        entity.doctypedesc = document.description
        entity.parentid = entity.objid
        entity.parentorno = entity.orno
        entity.redemptiondate = dtSvc.serverDate
    }

    public def open() {
        entity = svc.open(entity.objid)
        mode = 'saved'
        return "default";
    }

    public void save() {
        entity = svc.supersedeDoc( entity )
        mode = 'saved'
        MsgBox.alert("Successfully saved.    ")
    }

    def preview() {
        report.viewReport()
        return "preview";
    }

    def back() {
        return "default";
    }

    def sdf = new java.text.SimpleDateFormat( "MMMMM dd, yyyy")
    def sdf1 = new java.text.SimpleDateFormat( "yyyy-MM-dd")

    def report = [
        getReportName : { return 'demandletters/noticeofredemption' }, 
        getReportData : { 
            entity.redemptionordate = sdf.format( sdf1.parse(entity.redemptionordate))
            entity.auctiondate = sdf.format( sdf1.parse(entity.auctiondate))
            entity.redemptiondate = sdf.format( sdf1.parse(entity.redemptiondate))
            entity.parenttxndate = sdf.format( sdf1.parse(entity.parenttxndate))
            entity.land = (entity.rputype == 'land') ? 'X' : ' '
            entity.mach = (entity.rputype == 'mach') ? 'X' : ' '
            entity.improvement = (entity.rputype.matches("bldg|misc|plant")) ? 'X' : ' ';
            entity.delinquent = (entity.redeembyinfo == "1") ? 'X':' ';
            entity.representative = (entity.redeembyinfo == "2") ? 'X':' ';
            entity.otherperson = (entity.redeembyinfo == "3") ? 'X':' ';
            return entity  
        },
        getParameters : {
            def params = paramSvc.getStandardParameter( OsirisContext.env.USERNAME )
            return params 
        }
    ]  as ServerReportModel

}
